    cmd = command = ''
    parallel = type_group(sgname,sysobj)
    tmpcmd = '_cmd_sleep 10; _cmd_hagrp -state ' + sgname + ' | _cmd_grep ONLINE | _cmd_wc -l'
    index = 0
    while (index < 10): #wait for 100s
        rc, num = sysobj.cmd(tmpcmd)
        if parallel == 0 and num == '1':
            break
        elif parallel == 1 and num == str(len(systems)):
            break
        else:
            sysobj.cmd(tmpcmd)
        index += 1
    if index == 10:
        return 0
    return 1

