def get_clus_uuid(sysobj):
    rootpath = sysobj.args.get('rootpath') or ''
    maincf = rootpath + '/etc/VRTSvcs/conf/config/main.cf'
    conf = {}
    with open(rootpath+'/etc/VRTSvcs/conf/sysname', 'r') as fh:
        sysname= fh.read()
        conf['sysname'] = sysname.rstrip('\n')
    with open(rootpath+'/etc/vx/.uuids/clusuuid', 'r') as fh:
        uuid = fh.read()
        conf['uuid'] = uuid.rstrip('\n')

    conf['clusname'] = sysobj.args.get('cluster_name')
    if not conf['clusname']:
        rc, output = sysobj.cmd("_cmd_grep '^cluster' " + maincf + ' 2> /dev/null')
        # get cluster name
        string = re.search('^cluster\s+(\S+)\s*', output)
