    def upgrade_precheck(self,sysobj):
        params = sysobj.args['parameter']
        self.large_lun_check(sysobj)

        #fix e1518138 : uninstall AT pkgs when upgrade VM/VVR
        component = params['host_facters']['component']
        if component.upper() in ['VM', 'DMP']:
            self.obsoleted_bundles_sys(sysobj)
            self.obsoleted_bundled_pkgs_sys(sysobj)

        self.obsoleted_thirdparty_pkgs_sys(sysobj)
        # check if dg has latest backup before upgrade
        if 'upgrade_phase' in params.keys() and params['upgrade_phase'] == 'upgrade_nonkernelpkgs':
            self.dg_conf_upgrade_precheck_sys(sysobj)

        if self.is_encapsulated_bootdisk_sys(sysobj):
            if not self.encap_upgrade_precheck_sys(sysobj):
                return ''

        # set donotstop and donotremove flag here.
        self.set_flag_encap_upgrade_sys(sysobj)
        sysobj.warning.append({'upgrade_precheck_encapsulated_bootdisk': 'Encapsulated bootdisk upgrade requires a reboot after upgrading'})

        # check if any cache objects enabled on the system
        if 'upgrade_phase' in params.keys() and params['upgrade_phase'] == 'upgrade_nonkernelpkgs':
            self.cache_areas_sys(sysobj)
        if 'upgrade_phase' in params.keys() and params['upgrade_phase'] not in ['upgrade_kernelpkgs', 'upgrade_nonkernelpkgs']:
            self.open_volumes_sys(sysobj,'upgrade')
        if self.vr_licensed_sys(sysobj):
            self.vvr_upgrade_precheck_sys(sysobj)

