    def backup_vm_configfiles(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''} 
        rc, output = sysobj.cmd("_cmd_mkdir -p " + sysobj.tmpdir + "/VXVM-CFG-BAK/")
        if rc != 0:
            rtn.rc = rc
            rtn.msg = output
            return rtn

        for file in self.configfiles:
            if os.path.exists(file):
                sysobj.cmd("_cmd_cp -pf " + file + " " + sysobj.tmpdir + "/VXVM-CFG-BAK/")

        return rtn

    def upgrade_pre_uninstall(self, sysobj):
        rtn = self.backup_vm_configfiles(sysobj)
        return rtn['rc']

    def upgrade_post_uninstall(self, sysobj):
        if callable(getattr(self, 'backup_vxvm_tunables', None)):
            rtn = getattr(self, 'backup_vxvm_tunables')(sysobj)
        return rtn

    def upgrade_post_install(self, sysobj):
        ## restore the config files
        for cf in self.configfiles:
            filepath,filename = os.path.split(cf)
            old = sysobj.tmpdir + '/VXVM-CFG-BAK/' + filename
            if not os.path.exists(old):
                continue
            if filename == 'vxdmp.conf':
                rc,output = sysobj.cmd("_cmd_grep '0x3FFFF' " + old +' 2>/dev/null')
                if output:
                    continue
            if filename == 'vxio.conf' or filename == 'vxdmp.conf' or filename == 'vxvm-recover.conf':
                # e3623338 Modify the permission of vxio.conf/vxdmp.conf/vxvm-recover.conf to 0644 root sys
                sysobj.cmd("_cmd_chmod 0644 " + old + ' 2>/dev/null;_cmd_chown root:sys ' + old + '2 >/dev/null')
            sysobj.cmd("_cmd_cp -pf " + old + ' ' + cf)
        if os.path.exists(self.volbootfile):
            sysobj.cmd("_cmd_rmr " + self.mkdbfile)
        #sysobj.cmd("_cmd_rmr " + sysobj.tmpdir + '/VXVM-CFG-BAK/')

