    def backup_vxvm_tunables(self, sysobj):
        rtn = {'changed': 0, 'rc': 0, 'msg': ''} 
        sourcefile = "/etc/vx/vxvm_tunables.rpmsave"
        upgradefile = "/etc/vx/vxvm_tunables.cpiupgrade"
        if os.path.exists(sourcefile):
            sysobj.cmd("_cmd_rm -f " + upgradefile)
            sysobj.cmd("_cmd_cp -p " + sourcefile + " " + upgradefile)
        return rtn

    def check_config(self,sysobj):
        sysobj.log.debug('VM check_config called')
        # VM is configured if SF/SFHA/SFCFSHA/FOUNDATION/STORAGE/ENTERPRISE is installed and mkdbfile does not exist
        VM_PROD_LIST = ['SF','SFHA','SFCFSHA','FOUNDATION','STORAGE','ENTERPRISE']
        installed_prod = sysobj.args['parameter']['host_facters']['product']
        prod_matched = 0
        if installed_prod == '':
            return 0
        for i in VM_PROD_LIST:
            if i == installed_prod.upper():
                prod_matched = 1
        if prod_matched == 0:
            return 0
        if os.path.exists(self.mkdbfile):
            return 0

        return 1
    

