class VRTSsfmh_common(Base_pkg, object):
    def __init__(self):
        super(VRTSsfmh_common,self).__init__()
        self.start_procs = ['sfmh_discovery','xprtld']
        self.stop_procs = ['sfmh_discovery','vxdclid']
        self.version = ''
        self.pkg = 'VRTSsfmh'
        self.is_kernel = False
        pass
   
    def upgrade_pre_uninstall(self, sysobj):
        pass

    def upgrade_post_uninstall(self, sysobj):
        pass

    def upgrade_pre_install(self, sysobj):
        pass

    def upgrade_post_install(self, sysobj):
        proc = instance_factory(sysobj, 'vxdclid', None, 'proc')
        # e3712926
        if proc.check_proc(sysobj):
            sysobj.cmd("/opt/VRTSsfmh/etc/vxdcli.sh stop")
        pass

    def check_if_managedhost_connected_to_central_server(self,sysobj):
        rc,output = sysobj.cmd("/opt/VRTSsfmh/bin/xprtlc -l https://localhost:5634/world/getvitals 2>/dev/null")
        mgmt_servers = []
        if not rc:
            json_output = eval(output)
            if json_output['DOMAINS']:
                sysobj.log.info("The node is reported to VOM server: " + json_output['DOMAINS'])
                return 1
        else:
            # xprtld is not running on the system, checking '/etc/default/sfm_resolv.conf'
            rc,output = sysobj.cmd("_cmd_cat " + "/etc/default/sfm_resolv.conf")
            for line in output.split('\n'):
                ma = re.search('\[(.*)\]', line)
                if ma:
                    mgmt_server = ma.group(1)
                    ma = re.search(r'.*://(.*):\d+.*$',mgmt_server)
                    if ma.group(1) !=  'domains':
                        mgmt_servers.append(ma.group(1))
            sysobj.log.info("xprtld is not running, the VOM server: " + mgmt_servers)
            return ' '.join(mgmt_servers)

